const addToCartButtons=document.querySelectorAll(".addToCart"),cartContent=document.querySelector("#cart ul"),cartTotal=document.querySelector("#cartTotal"),totalAmount=document.querySelector("#totalAmount"),cart=document.querySelector("#cart"),cartIcon=document.querySelector("#cartIcon"),cartForm=document.querySelector("#cartForm");cartIcon.addEventListener("mouseover",()=>{cartForm.style.display="block"}),cart.addEventListener("mouseleave",()=>{cartForm.style.display="none"}),addToCartButtons.forEach(button=>{button.addEventListener("click",event=>{productAdded=products.find(product=>product.productId===event.target.dataset.id),cartContent.innerHTML+=`<li>\n        ${productAdded.name} - 1 db - ${(new Intl.NumberFormat).format(productAdded.price)}\n        <input type="hidden" name="productId[]" value="${productAdded.productId}">\n        <input type="hidden" name="product[]" value="${productAdded.name}">\n        <input type="hidden" name="price[]" value="${productAdded.price}">\n    </li>`,cartTotal.value=parseInt(cartTotal.value)+parseInt(productAdded.price),totalAmount.innerHTML=(new Intl.NumberFormat).format(cartTotal.value),cartIcon.classList.add("dropin"),setTimeout(()=>cartIcon.classList.remove("dropin"),500)})});